import React, { Fragment, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Axios from "axios";
import { API_ROUTES } from '../app-modules/api_routes';
import "./login.css";

const Loginform = () => {
  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const login = () => {
    setError(""); // Clear any previous errors
    Axios.post(API_ROUTES.login, {
      email: email,
      password: password,
    }).then((response) => {
      if (!response.data.auth) {
        setError(response.data.message || "An error occurred"); // Display the error message from the server, or a generic error message
      } else {
        navigate("/dashboard");
        localStorage.setItem("token", response.data.token);
      }
    }).catch((error) => {
      setError("An error occurred while logging in"); // Display generic error message for network or other errors
    });
  };

  return (
    <Fragment>
      <div className="loginform">
        <div className="emailinp">
          <label>
            <h3>Email</h3>
          </label>
          <br />
          <input
            type="text"
            required
            placeholder="id"
            onChange={(e) => {
              setemail(e.target.value);
            }}
          />
        </div>
        <div className="password">
          <label>
            <h3>Password</h3>
          </label>
          <br />
          <input
            type="password"
            required
            placeholder="password"
            onChange={(e) => {
              setpassword(e.target.value);
            }}
          />
        </div>
        {error && <p className="error-message">{error}</p>}
        <div className="btnloginform">
          <span className="loginbtn">
            <button onClick={login} style={{ cursor: 'pointer' }}>Login</button>
          </span>
          <span className="loginbtn">
            <Link to='/dashboard'>
              <button>Cancel</button>
            </Link>
          </span>
          <div className="already">
          <p>
            don't have an account? <Link to="/signup">Log In</Link>
          </p>
        </div>
        </div>
      </div>
    </Fragment>
  );
};

export default Loginform;
