import React, { useState, useEffect, Fragment } from 'react';
import axios from 'axios';
import Navbar from "../components/navbar";
import notiIcon from '../images/icons8-notifications-64.png';
import settingsIcon from '../images/icons8-settings-50.png';
import menuIcon from '../images/icons8-menu-50.png';
import './buildChatflow.css';
import { API_ROUTES } from '../app-modules/api_routes';

const BuildChatFlow = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [apps, setApps] = useState([]);
  const [error, setError] = useState('');
  const [chatFlows, setChatFlows] = useState([{ id: 1, trigger: '', response: '' }]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  useEffect(() => {
    const fetchApps = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Token not found in local storage');
        return;
      }

      try {
        const response = await axios.post(API_ROUTES.fetchUserApps, { token });
        setApps(response.data.apps);
      } catch (error) {
        setError(error.response?.data?.error || 'Error fetching apps');
      }
    };

    fetchApps();
  }, []);

  const handleAddChatFlow = () => {
    setChatFlows([...chatFlows, { id: chatFlows.length + 1, trigger: '', response: '' }]);
  };

  const handleChatFlowChange = (id, field, value) => {
    const updatedChatFlows = chatFlows.map((chatFlow) => 
      chatFlow.id === id ? { ...chatFlow, [field]: value } : chatFlow
    );
    setChatFlows(updatedChatFlows);
  };

  const handleSaveChatFlows = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Token not found in local storage');
        return;
      }

      await axios.post(API_ROUTES.saveChatFlows, { token, chatFlows });
      alert('Chat flows saved successfully');
    } catch (error) {
      setError(error.response?.data?.error || 'Error saving chat flows');
    }
  };

  return (
    <Fragment>
      <div className="div_main_dashboard">
        <div className="dashboard_header_container">
          <header className="dashboard_header">
            <div className="header_left">
              <img src={menuIcon} alt="Menu" className="menu-icon" onClick={toggleMenu} />
              <h1>Dashboard</h1>
            </div>
            <div className="header_search">
              <input type="text" placeholder="Search..." className="search_input" />
            </div>
            <div className="header_buttons">
              <button className="header_button">
                <img src={settingsIcon} alt="Settings" />
              </button>
              <button className="header_button">
                <img src={notiIcon} alt="Notifications" />
              </button>
            </div>
          </header>
        </div>
        <Navbar isMenuOpen={isMenuOpen} toggleMenu={toggleMenu} />
        
      </div>
    </Fragment>
  );
};

export default BuildChatFlow;
