import React, { useState, useEffect, Fragment } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Navbar from "../components/navbar";
import notiIcon from '../images/icons8-notifications-64.png';
import settingsIcon from '../images/icons8-settings-50.png';
import menuIcon from '../images/icons8-menu-50.png';
import './buildChatflow.css';
import { API_ROUTES } from '../app-modules/api_routes';

const BuildChatFlow = () => {
  const { app_id } = useParams();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [error, setError] = useState('');

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };


  const handleChatFlowChange = (id, field, value) => {
    const updatedChatFlows = chatFlows.map((chatFlow) => 
      chatFlow.id === id ? { ...chatFlow, [field]: value } : chatFlow
    );
    setChatFlows(updatedChatFlows);
  };

  const handleSaveChatFlows = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Token not found in local storage');
        return;
      }

      await axios.post(API_ROUTES.saveChatFlows, { token, app_id, chatFlows });
      alert('Chat flows saved successfully');
    } catch (error) {
      setError(error.response?.data?.error || 'Error saving chat flows');
    }
  };

  return (
    <Fragment>
      <div className="div_main_dashboard">
        <div className="dashboard_header_container">
          <header className="dashboard_header">
            <div className="header_left">
              <img src={menuIcon} alt="Menu" className="menu-icon" onClick={toggleMenu} />
              <h1>Dashboard</h1>
            </div>
            <div className="header_search">
              <input type="text" placeholder="Search..." className="search_input" />
            </div>
            <div className="header_buttons">
              <button className="header_button">
                <img src={settingsIcon} alt="Settings" />
              </button>
              <button className="header_button">
                <img src={notiIcon} alt="Notifications" />
              </button>
            </div>
          </header>
        </div>
        <Navbar isMenuOpen={isMenuOpen} toggleMenu={toggleMenu} />
        <div className="chatflow-container">
          <h2>Build Your Chat Flow</h2>
          {chatFlows.map((chatFlow) => (
            <div key={chatFlow.id} className="chatflow-item">
              <input
                type="text"
                placeholder="Trigger Message"
                value={chatFlow.trigger}
                onChange={(e) => handleChatFlowChange(chatFlow.id, 'trigger', e.target.value)}
                className="chatflow-input"
              />
              <textarea
                placeholder="Response Message"
                value={chatFlow.response}
                onChange={(e) => handleChatFlowChange(chatFlow.id, 'response', e.target.value)}
                className="chatflow-textarea"
              />
            </div>
          ))}
          <button onClick={handleAddChatFlow} className="add-chatflow-btn">Add Chat Flow</button>
          <button onClick={handleSaveChatFlows} className="save-chatflow-btn">Save Chat Flows</button>
        </div>
        {error && <p className="error-message">{error}</p>}
      </div>
    </Fragment>
  );
};

export default BuildChatFlow;
