import React, { useState, useEffect, Fragment } from 'react';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';
import Navbar from "../components/navbar";
import notiIcon from '../images/icons8-notifications-64.png';
import settingsIcon from '../images/icons8-settings-50.png';
import menuIcon from '../images/icons8-menu-50.png';
import './manageflow.css';
import { API_ROUTES } from '../app-modules/api_routes';

const Manageflow = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [appDetails, setAppDetails] = useState(null);
    const [phoneNumber, setPhoneNumber] = useState('');
    const [error, setError] = useState('');
    const { app_id } = useParams();

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    useEffect(() => {
        const fetchAppDetails = async () => {
            try {
                const response = await axios.get(`${API_ROUTES.APP_DETAILS}/${app_id}`);
                setAppDetails(response.data);
            } catch (error) {
                setError('Error fetching app details');
            }
        };

        fetchAppDetails();
    }, [app_id]);
    const handleSetWebhook = async () => {
        if (loading) return; // Prevent duplicate requests

        setLoading(true); // Set loading state to true
        try {
            const response = await axios.post('http://localhost:8080/webhook', { appId: '400576639632212' });
            console.log(response.data.message);
            setError('');
        } catch (error) {
            setError(error.response?.data?.error || 'Error setting webhook');
        } finally {
            setLoading(false); // Reset loading state regardless of success or failure
        }
    };


    const handleAddPhoneNumber = async () => {
        try {
            await axios.post(API_ROUTES.ADD_PHONE, { app_id, phoneNumber });
            alert('Phone number added successfully');
        } catch (error) {
            setError('Error adding phone number');
        }
    };

    if (!appDetails) {
        return <p>Loading...</p>;
    }

    return (
        <div className="div_main_dashboard">
            <div className="dashboard_header_container">
                <header className="dashboard_header">
                    <div className="header_left">
                        <img src={menuIcon} alt="Menu" className="menu-icon" onClick={toggleMenu} />
                        <h1>Dashboard</h1>
                    </div>
                    <div className="header_search">
                        <input type="text" placeholder="Search..." className="search_input" />
                    </div>
                    <div className="header_buttons">
                        <button className="header_button">
                            <img src={settingsIcon} alt="Settings" />
                        </button>
                        <button className="header_button">
                            <img src={notiIcon} alt="Notifications" />
                        </button>
                    </div>
                </header>
            </div>
            <Navbar isMenuOpen={isMenuOpen} toggleMenu={toggleMenu} />
            <div className="manage-flow-container">
                <h2>Manage Flow</h2>
                <button className="set-webhook-btn" onClick={handleSetWebhook} disabled={loading}>
                    {loading ? 'Setting Webhook...' : 'Set Webhook'}
                </button>
                {error && <p className="error-msg">{error}</p>}
            </div>
        </div>
    );
};

export default Manageflow;
