import React from "react";
import '../App.css'
import { Link } from "react-router-dom";
import shareIcon from '../Images/shareIcon.png'
import insta from '../Images/icons8-instagram-50.png'
import JSZip from 'jszip';
import ProductImg from '../Images/Black_Pearl.png'

const FreeFiles1 = [
  'https://dropment.online/Free_Instagram_theme_page_bundle_no1.zip'
];

const FreeFiles2 = [
  'https://dropment.online/Free_Instagram_theme_page_bundle_no2.zip'
];

const FreeFiles3 = [
  'https://dropment.online/Free_Instagram_theme_page_bundle_no3.zip'
];

const Product1 = () => {

  const handleShare = async () => {
    const siteURL = 'https://dropment.online/Black-Pearl'; // URL of dropment.online
  
    if (navigator.share) {
      try {
        await navigator.share({
          title: 'Check out Dropment!',
          text: `Discover cool motivational clips and download them for free to grow your Instagram page at Dropment.`,
          url: siteURL
        });
      } catch (error) {
        console.error('Error sharing:', error);
      }
    } else {
      // Fallback for browsers that don't support Web Share API
      const shareText = `Check out Dropment: Discover cool motivational clips and download them for free to grow your Instagram page at ${siteURL}`;
      const shareURL = `whatsapp://send?text=${encodeURIComponent(shareText)}`;
      window.location.href = shareURL;
    }
  };

  const downloadFile = (url) => {
    const fileName = url.split('/').pop();
    const aTag = document.createElement('a');
    aTag.href = url;
    aTag.setAttribute('download', fileName);
    document.body.appendChild(aTag);
    aTag.click();
    aTag.remove();
  }

  return (
    <div className="main_div_products_page">
      <div className="header_products_page">
        <Link to='/'>
          <button>&#9664;</button>
        </Link>
        <h2>Black Pearl</h2>
        <div className="icons">
          <i className="fa fa-share-alt"><img src={shareIcon} onClick={handleShare} alt="Share icon"/></i>
          <i className="fa fa-instagram"><img src={insta} alt="Instagram icon" onClick={() => {
            // Assuming you only want to download the first file from FreeFiles1 array
            downloadFile(FreeFiles1[0]);
          }}/></i>
        </div>
      </div>
      <div className="section_1_product">
      <div className="image_product1">
          <img src={ProductImg}/>
      </div>
      <div className="Details_product_No1">
        <h2>Black Pearl</h2>
        <h3>free!</h3>
        <button>Buy Now</button>
      </div>
      <div className="DetailsProduct"></div>
      </div>
    </div>
  );
};

export default Product1;
